func ParseStatus(s string) (string, error) {
	switch strings.ToLower(s) {
	case "pending", "PENDING", "Pending":
		return "Pending", nil
	case "success", "SUCCESS", "Success":
		return "Success", nil
	case "failed", "FAILED", "Failed":
		return "Failed", nil
	default:
		return "Failed", fmt.Errorf("invalid status: #{s}", s)
	}
}
